func stringToQueryId(url string) [32]byte {
	var id = [32]byte{}
	sha := sha3.NewKeccak256()
	_, err := sha.Write([]byte(url))
	Expect(err).ToNot(HaveOccurred())

	idSlice := sha.Sum(nil)
	Expect(len(idSlice)).To(Equal(32))

	copy(id[:], idSlice)

	return id
}

